改进版的代码：
<div>

#include&lt;stdio.h&gt;
#include&lt;stdlib.h&gt;
#include&lt;sys/wait.h&gt;

//默认执行的秒数
#define TIMES 60

//默认输出的文件路径
#define LOGFILE "temperature.log"

//循环条件，传入-1将无限循环
#define LOOPCONDITION(x)(x==-1?1:x)

//显示错误信息并退出程序
void error(char *msg)
{
puts(msg);
exit(1);
}

/*
* argc 执行的秒数（次数）
* argv 重定向到新文件的路径（名称）
*/
int main(int argc,char *argv[])
{
int times;
FILE *f_log;

if(argc==1)
{
times=TIMES;
f_log=fopen(LOGFILE,"a+");
}
else if(argc==2)
{
//初始化执行的次数
if(atoi(argv[1]))
times=atoi(argv[1]);
else
fopen(argv[1],"a+");
}
else if(argc==3)
{
if(atoi(argv[1]))
times=atoi(argv[1]);
else
error("Invalid times formate");

f_log=fopen(argv[2],"a+");
}
else
error("Unkow argument format");

//克隆当前进程，产生子进程
pid_t pid=fork();

if(pid==-1)
error("Cannot fork process");

//创建父子进程连接管道
int fd[2];
if(pipe(fd)==-1)
error("Cannot create pipe");

//子进程
if(!pid)
{
//关闭子进程的输入管道
close(fd[0]);

//将子进程的输出管道重定向到f_log的输入管道
dup2(fileno(f_log),1);
}

int status;
while(LOOPCONDITION(times))
{
//system调用系统文件，默认输出将重定向至文件指针指向的文件
//获取主板温度
system("/opt/vc/bin/vcgencmd measure_temp");

//获取系统时间
system("date");

times--;
sleep(1);
}

//关闭文件指针
fclose(f_log);

return 0;
}

