/**
*    Matrix寄存器地址常量
*/
const byte NOOP=0x0;        //不运行
const byte DECODEMODE=0x9;    //译码模式
const byte INTENSITY=0xA;    //显示强度
const byte SCANLIMIT=0xB;    //扫描限制
